sys_variable_value.value:
(function execute(inputs, outputs) {
  var inputScope = inputs.flow_scope_sys_id;
  var newFlow = inputs.new_flow_sys_id;
  var folderPath = inputs.folder_path;
  var boxConnection = inputs.box_connection;
  var triggerTable = inputs.trigger_table;

  //update trigger table
  sn_flow.FlowTemplates.updateFlowTrigger(newFlow, 'condition', "table_name="+triggerTable);

  // update folderpath onedrive
  sn_flow.FlowTemplates.updateFlowActions(newFlow, '1', 'folder_path', folderPath);

  new global.FlowTemplatesHelper().activateConnection(boxConnection);
})(inputs, outputs);

