sys_variable_value.value:
(function execute(inputs, outputs) {
  var inputScope = inputs.flow_scope_sys_id;
  var newFlow = inputs.new_flow_sys_id;
  var folderName = inputs.file_or_folder_name;
  var fileDescription = inputs.file_description;
  var googledriveConnection = inputs.googledrive_connection;
  var triggerTable = inputs.trigger_table;

  //update trigger table name
  sn_flow.FlowTemplates.updateFlowTrigger(newFlow, 'condition', "table_name="+triggerTable);

  // update folderpath onedrive
  sn_flow.FlowTemplates.updateFlowActions(newFlow, '1', 'file_or_folder_name', folderName);

  // update folderpath onedrive
  sn_flow.FlowTemplates.updateFlowActions(newFlow, '3', 'file_description', fileDescription);

  new global.FlowTemplatesHelper().activateConnection(googledriveConnection);
})(inputs, outputs);

